mapper.select:
    select

    from permission
    where domain_id = #{domainId, jdbcType=INTEGER}

       and value like concat(concat('%', #{value, jdbcType=VARCHAR}),'%')  


		and status = #{status, jdbcType=TINYINT}


		and perm_type_id = #{permTypeId, jdbcType=INTEGER}

	order by id limit #{startIndex, jdbcType=INTEGER},#{wantCount, jdbcType=INTEGER}

    select
    	count(1)
    from permission
    where domain_id = #{domainId, jdbcType=INTEGER}

       and value like concat(concat('%', #{value, jdbcType=VARCHAR}),'%')  


		and status = #{status, jdbcType=TINYINT}


		and perm_type_id = #{permTypeId, jdbcType=INTEGER}

select

from permission p
where p.status = 0
and p.domain_id =  #{domainId, jdbcType=INTEGER}
and p.id in(
	select rp.permission_id
	from role_permission rp
	where rp.role_id = #{roleId, jdbcType=INTEGER}
)
