mapper.select:
	SELECT * FROM(SELECT t2.id id,t2.user_id userId,t1.extend_id extendId,t2.value_ value,t2.status status,
		t1.extend_code extendCode,t1.extend_description extendDescription FROM 
		(SELECT id extend_id,code extend_code,description extend_description FROM user_extend 
			WHERE 1=1

		  		AND `code` LIKE #{extendCode, jdbcType=VARCHAR}

		) t1
	LEFT JOIN 
		(SELECT * FROM user_extend_val WHERE user_id=#{userId} ORDER BY id DESC)t2 
	ON  t1.extend_id=t2.extend_id) tt1 GROUP BY extendId ORDER BY userId DESC

      limit ${pageNumber},${pageSize}
